let submitBtn=document.querySelector(".contact__submit"),form=document.querySelector(".contact__form");function validateEmail(e){if(/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(String(e.value).toLowerCase()))return e.style.borderColor="#868686",!0;e.style.borderColor="#ff0000"}submitBtn.addEventListener("click",(()=>{let e=document.querySelector("#full_name"),t=document.querySelector("#email"),o=document.querySelector("#subject"),r=document.querySelector("#message");checkForNull(e)&&validateEmail(t)&&checkForNull(o)&&checkForNull(r)&&form.submit()}));