const body=document.querySelector("body"),projectsBlock=document.querySelector(".projects");projectsBlock.addEventListener("click",(e=>{let t=e.target.parentElement;if(t.classList.contains("projects__item")){let e=document.querySelectorAll(".projects__item");t.classList.contains("active")?(Array.from(e).filter((e=>e.classList.contains("active"))).map((e=>e.classList.remove("active"))),body.style.overflowY="auto"):(t.classList.add("active"),body.style.overflowY="hidden")}}));